using Xunit;

namespace romansNumberGenerator
{
    public class Test
    {
        private RomanNumberGenerator rnc;

        [Theory]
        [InlineData("19845", "MMMMMMMMMMMMMMMMMMMDCCCXLV")]
        [InlineData("356", "CCCLVI")]
        [InlineData("19845", "MMMMMMMMMMMMMMMMMMMDCCCXLV")]
        [InlineData("19458", "MMMMMMMMMMMMMMMMMMMCDLVIII")]
        [InlineData("8965427", "")]
        [InlineData("563", "DLXIII")]
        public void TestGenerateNumber(string arabicNumber, string romanNumber)
        {
            rnc = new RomanNumberGenerator();


            Assert.Equal(rnc.generateRomanNumber(arabicNumber), romanNumber);
        }
    }
}

